import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";

export default function Register() {
  const [form, setForm] = useState({ username: "", email: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      // Port 8081 use කරන්න
      const res = await fetch("http://localhost:8081/user/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });
      
      const data = await res.json();
      
      if (data.status === "success") {
        alert(data.message);
        // Registration success නම් login page එකට redirect කරන්න
        navigate("/login");
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Registration error:", error);
      alert("Something went wrong. Please try again.");
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "400px", margin: "auto" }}>
        <h2>Register</h2>
        <form onSubmit={handleSubmit}>
            <input name="username" placeholder="Username" value={form.username} onChange={handleChange} required/><br /><br />
            <input name="email" placeholder="Email" type="email" value={form.email} onChange={handleChange} required/><br /><br />
            <input name="password" placeholder="Password" type="password" value={form.password} onChange={handleChange} required/><br /><br />
            <button type="submit">Register</button>
        </form>
      
        {/* Login link */}
        <div style={{ marginTop: "20px", textAlign: "center" }}>
            <p>Already have an account? 
            <Link to="/login" style={{ color: "#007bff", textDecoration: "none", marginLeft: "5px"}}>
            Login here
            </Link>
            </p>
        </div>
    </div>
  );
}
