import React, { useState, useEffect, useRef } from 'react';
import { Search, Calendar, MapPin, Users, Clock, Star, ChevronDown, Menu, X, Bus, Shield, CreditCard, Phone, Play, CheckCircle, ArrowRight, Smartphone, QrCode, User, LogOut } from 'lucide-react';
import { Link } from 'react-router-dom';

export default function Home() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [fromLocation, setFromLocation] = useState('');
  const [toLocation, setToLocation] = useState('');
  const [date, setDate] = useState('');
  const [passengers, setPassengers] = useState(1);
  const [isScrolled, setIsScrolled] = useState(false);
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  const [userInfo, setUserInfo] = useState({ username: '', email: '' });
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Fetch user info from backend
  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        // Check if backend is available first
        const response = await fetch('/api/user/profile', {
          method: 'GET',
          credentials: 'include',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        console.log('Profile fetch status:', response.status);

        if (response.ok) {
          const contentType = response.headers.get('content-type');
          if (contentType && contentType.includes('application/json')) {
            const userData = await response.json();
            setUserInfo({
              username: userData.username || 'User',
              email: userData.email || 'user@example.com'
            });
            console.log('User info loaded:', userData);
          } else {
            console.log('Backend returned HTML instead of JSON - endpoint may not exist');
            // Backend endpoint නැති නම් default values use කරන්න
            setUserInfo({
              username: 'Test User',
              email: 'test@example.com'
            });
          }
        } else {
          console.log('Failed to fetch user info, status:', response.status);
          setUserInfo({
            username: 'Guest User',
            email: 'guest@example.com'
          });
        }
      } catch (error) {
        console.error('Network error fetching user info:', error);
        // Network error නම් default values
        setUserInfo({
          username: 'Default User',
          email: 'default@example.com'
        });
      }
    };

    fetchUserInfo();
  }, []);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsProfileDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleLogout = async (e) => {
    e.preventDefault();
    e.stopPropagation();
    
    try {
      console.log('Logout button clicked - starting logout process');
      
      // Call backend logout endpoint
      const response = await fetch('/api/auth/logout', {
        method: 'POST',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      console.log('Logout response status:', response.status);
      
      if (response.ok) {
        console.log('Logout successful, redirecting...');
      } else {
        console.log('Logout failed with status:', response.status);
      }

      // Redirect to login page regardless of backend response
      console.log('Redirecting to login page');
      window.location.href = '/login';
      
    } catch (error) {
      console.error('Network error during logout:', error);
      console.log('Network error occurred, still redirecting to login page');
      window.location.href = '/login';
    }
    
    setIsProfileDropdownOpen(false);
  };

  const steps = [
    {
      icon: Search,
      title: 'Search Routes',
      description: 'Find your perfect bus route from hundreds of available options across Sri Lanka'
    },
    {
      icon: Calendar,
      title: 'Book & Pay',
      description: 'Select your preferred time and complete secure payment in just a few clicks'
    },
    {
      icon: Smartphone,
      title: 'Digital Ticket',
      description: 'Get instant QR code tickets on your phone - no printing required'
    }
  ];

  const features = [
    {
      icon: CheckCircle,
      title: 'Real-time tracking',
      description: 'Track your bus location live and get arrival updates'
    },
    {
      icon: CheckCircle,
      title: 'Instant refunds',
      description: 'Cancel anytime and get instant refunds to your account'
    },
    {
      icon: CheckCircle,
      title: 'Digital tickets only',
      description: 'Eco-friendly paperless tickets with QR code scanning'
    }
  ];

  const guidelines = [
    {
      icon: Bus,
      title: 'Easy Booking',
      description: 'Book your bus tickets in just 3 simple steps with our user-friendly platform'
    },
    {
      icon: Shield,
      title: 'Secure Payment',
      description: 'Your payment information is protected with bank-level security encryption'
    },
    {
      icon: Clock,
      title: 'Trip Availability',
      description: 'Real-time availability updates ensure you never miss your preferred departure'
    },
    {
      icon: Phone,
      title: 'Mobile Support',
      description: 'Access all features on mobile with our responsive design and mobile app'
    }
  ];

  return (
    <div className="min-h-screen bg-white">
      {/* Navigation */}
      <nav className={`fixed top-0 w-full z-50 transition-all duration-300 ${isScrolled ? 'bg-white/10 backdrop-blur-lg shadow-lg' : 'bg-white/5 backdrop-blur-sm'
        }`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-r from-blue-600 to-blue-700 rounded-lg flex items-center justify-center">
                <Bus className="w-6 h-6 text-white" />
              </div>
              <span className="text-xl font-bold text-gray-900">ProjectSlb</span>
            </div>

            {/* Desktop Menu */}
            <div className="hidden md:flex items-center space-x-8">
              <Link to="/" className="text-gray-300 hover:text-blue-600 transition-colors font-medium">Home</Link>
              <Link to="/routes" className="text-gray-300 hover:text-blue-600 transition-colors font-medium">Routes</Link>
              <Link to="/buytickets" className="text-gray-300 hover:text-blue-600 transition-colors font-medium">Buy Season</Link>
              <Link to="/help" className="text-gray-300 hover:text-blue-600 transition-colors font-medium">Help</Link>
              
              {/* Profile Dropdown */}
              <div className="relative" ref={dropdownRef}>
                <button
                  onClick={() => setIsProfileDropdownOpen(!isProfileDropdownOpen)}
                  className="flex items-center space-x-2 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors font-medium"
                >
                  <User className="w-5 h-5" />
                  <ChevronDown className={`w-4 h-4 transition-transform duration-200 ${isProfileDropdownOpen ? 'rotate-180' : ''}`} />
                </button>

                {/* Dropdown Menu */}
                {isProfileDropdownOpen && (
                  <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 overflow-hidden">
                  <div className="py-1">
                    <div className="px-4 py-3 border-b border-gray-100">
                      <div className="text-sm font-medium text-gray-900">{userInfo.username}</div>
                      <div className="text-sm text-gray-500">{userInfo.email}</div>
                    </div>
                      <Link
                        to="/profile"
                        className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 transition-colors"
                        onClick={() => setIsProfileDropdownOpen(false)}
                      >
                        <User className="w-4 h-4 mr-3" />
                        My Profile
                      </Link>
                      <Link
                        to="/bookings"
                        className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 transition-colors"
                        onClick={() => setIsProfileDropdownOpen(false)}
                      >
                        <Calendar className="w-4 h-4 mr-3" />
                        My Bookings
                      </Link>
                      <button
                        onClick={handleLogout}
                        className="flex items-center w-full px-4 py-2 text-sm text-red-600 hover:bg-red-50 transition-colors"
                      >
                        <LogOut className="w-4 h-4 mr-3" />
                        Logout
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Mobile Menu Button */}
            <div className="md:hidden flex items-center space-x-4">
              {/* Mobile Profile Dropdown */}
              <div className="relative" ref={dropdownRef}>
                <button
                  onClick={() => setIsProfileDropdownOpen(!isProfileDropdownOpen)}
                  className="flex items-center bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 transition-colors"
                >
                  <User className="w-5 h-5" />
                </button>

                {/* Mobile Dropdown Menu */}
                {isProfileDropdownOpen && (
                  <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 overflow-hidden">
                  <div className="py-1">
                    <div className="px-4 py-3 border-b border-gray-100">
                      <div className="text-sm font-medium text-gray-900">{userInfo.username}</div>
                      <div className="text-sm text-gray-500">{userInfo.email}</div>
                    </div>
                      <Link
                        to="/profile"
                        className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 transition-colors"
                        onClick={() => setIsProfileDropdownOpen(false)}
                      >
                        <User className="w-4 h-4 mr-3" />
                        My Profile
                      </Link>
                      <Link
                        to="/bookings"
                        className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 transition-colors"
                        onClick={() => setIsProfileDropdownOpen(false)}
                      >
                        <Calendar className="w-4 h-4 mr-3" />
                        My Bookings
                      </Link>
                      <button
                        onClick={handleLogout}
                        className="flex items-center w-full px-4 py-2 text-sm text-red-600 hover:bg-red-50 transition-colors"
                      >
                        <LogOut className="w-4 h-4 mr-3" />
                        Logout
                      </button>
                    </div>
                  </div>
                )}
              </div>

              <button
                onClick={() => setIsMenuOpen(!isMenuOpen)}
                className="text-gray-700 hover:text-blue-600 transition-colors"
              >
                {isMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
              </button>
            </div>

            {/* Mobile Menu */}
            {isMenuOpen && (
              <div className="md:hidden absolute top-16 left-0 right-0 bg-white shadow-lg border-t border-gray-200">
                <div className="flex flex-col space-y-0">
                  <Link 
                    to="/" 
                    className="px-4 py-3 text-gray-700 hover:bg-gray-50 transition-colors border-b border-gray-100"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Home
                  </Link>
                  <Link 
                    to="/routes" 
                    className="px-4 py-3 text-gray-700 hover:bg-gray-50 transition-colors border-b border-gray-100"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Routes
                  </Link>
                  <Link 
                    to="/buytickets" 
                    className="px-4 py-3 text-gray-700 hover:bg-gray-50 transition-colors border-b border-gray-100"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Buy Season
                  </Link>
                  <Link 
                    to="/help" 
                    className="px-4 py-3 text-gray-700 hover:bg-gray-50 transition-colors"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Help
                  </Link>
                </div>
              </div>
            )}
          </div>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="relative min-h-screen flex items-center justify-center overflow-hidden">
        {/* Background Image */}
        <div className="absolute inset-0 z-0">
          <div
            className="w-full h-screen bg-cover bg-center bg-no-repeat"
            style={{
              backgroundImage: `linear-gradient(rgba(0,0,0,0.4), rgba(0,0,0,0.4)), url("https://kootook.com/wp-content/uploads/2021/06/bus-ticket.jpg?auto=format&fit=crop&w=1200&q=80")`,
              transform: "scaleX(-1)",
            }}
          >
            {/* Floating Bus Illustration */}
            <div className="absolute top-1/2 left-1/4 transform -translate-y-1/2 opacity-20">
              <div className="w-32 h-20 bg-blue-600 rounded-lg relative">
                <div className="absolute -top-2 left-4 right-4 h-8 bg-blue-700 rounded-t-lg"></div>
                <div className="absolute bottom-2 left-2 w-4 h-4 bg-gray-800 rounded-full"></div>
                <div className="absolute bottom-2 right-2 w-4 h-4 bg-gray-800 rounded-full"></div>
              </div>
            </div>
          </div>
        </div>

        <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
