import React, { useState, useEffect, useRef } from 'react';
import { Search, Calendar, Clock, ChevronDown, Menu, X, Bus, Shield, Phone, CheckCircle, Smartphone, User, LogOut, Globe } from 'lucide-react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next'; // 1. Meka import karanna

export default function Home() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  const [isLanguageDropdownOpen, setIsLanguageDropdownOpen] = useState(false); // New state for language dropdown
  const [userInfo, setUserInfo] = useState({ username: '', email: '' });

  // 2. useTranslation hook eka call karanna
  const { t, i18n } = useTranslation();

  // NEW: separate refs for desktop & mobile dropdowns
  const desktopProfileDropdownRef = useRef(null);
  const mobileProfileDropdownRef = useRef(null);
  const desktopLanguageDropdownRef = useRef(null); // New ref for desktop language dropdown
  const mobileLanguageDropdownRef = useRef(null); // New ref for mobile language dropdown

  useEffect(() => {
    const handleScroll = () => setIsScrolled(window.scrollY > 20);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Fetch user info from backend
  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const response = await fetch('http://localhost:8081/user/profile', {
          method: 'GET',
          credentials: 'include',
        });
        if (response.ok) {
          const userData = await response.json();
          setUserInfo({
            username: userData.username || 'User',
            email: userData.email || 'user@example.com',
          });
        } else {
          setUserInfo({ username: 'Guest User', email: 'guest@example.com' });
        }
      } catch (error) {
        setUserInfo({ username: 'Default User', email: 'default@example.com' });
      }
    };
    fetchUserInfo();
  }, []);

  // Close dropdowns when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        desktopProfileDropdownRef.current && !desktopProfileDropdownRef.current.contains(event.target) &&
        mobileProfileDropdownRef.current && !mobileProfileDropdownRef.current.contains(event.target)
      ) {
        setIsProfileDropdownOpen(false);
      }
      if (
        desktopLanguageDropdownRef.current && !desktopLanguageDropdownRef.current.contains(event.target) &&
        mobileLanguageDropdownRef.current && !mobileLanguageDropdownRef.current.contains(event.target)
      ) {
        setIsLanguageDropdownOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleLogout = async (e) => {
    e.preventDefault();
    e.stopPropagation();
    try {
      await fetch('/api/auth/logout', { method: 'POST', credentials: 'include' });
      window.location.href = '/login';
    } catch (error) {
      window.location.href = '/login';
    } finally {
      setIsProfileDropdownOpen(false);
    }
  };

  // 3. Language change function eka hadanna
  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
    setIsLanguageDropdownOpen(false); // Close dropdown after selection
  };

  const steps = [
    { icon: Search, title: t('steps.search.title'), description: t('steps.search.description') },
    { icon: Calendar, title: t('steps.book.title'), description: t('steps.book.description') },
    { icon: Smartphone, title: t('steps.digital.title'), description: t('steps.digital.description') }
  ];

  const features = [
    { icon: CheckCircle, title: t('features.tracking.title'), description: t('features.tracking.description') },
    { icon: CheckCircle, title: t('features.refunds.title'), description: t('features.refunds.description') },
    { icon: CheckCircle, title: t('features.digital.title'), description: t('features.digital.description') }
  ];

  const guidelines = [
    { icon: Bus, title: t('guidelines.booking.title'), description: t('guidelines.booking.description') },
    { icon: Shield, title: t('guidelines.payment.title'), description: t('guidelines.payment.description') },
    { icon: Clock, title: t('guidelines.availability.title'), description: t('guidelines.availability.description') },
    { icon: Phone, title: t('guidelines.support.title'), description: t('guidelines.support.description') }
  ];

  return (
    <div className="min-h-screen bg-white">
      {/* Navigation */}
      <nav className={`fixed top-0 w-full z-50 transition-all duration-300 ${isScrolled ? 'bg-white/10 backdrop-blur-lg shadow-lg' : 'bg-white/5 backdrop-blur-sm'}`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-r from-blue-600 to-blue-700 rounded-lg flex items-center justify-center">
                <Bus className="w-6 h-6 text-white" />
              </div>
              <span className="text-xl font-bold text-gray-900">ProjectSlb</span>
            </div>
      
            <div className="hidden md:flex items-center space-x-8">
              <Link to="/home" className="text-gray-300 hover:text-blue-600 transition-colors font-medium">{t('nav.home')}</Link>
              <Link to="/busroutes" className="text-blue-600 font-semibold">{t('nav.routes')}</Link>
              <Link to="/buytickets" className="text-gray-300 hover:text-blue-600 transition-colors font-medium">{t('nav.season')}</Link>
              <Link to="/help" className="text-gray-300 hover:text-blue-600 transition-colors font-medium">{t('nav.help')}</Link>
      
              {/* Desktop Language Dropdown (New) */}
              <div className="relative" ref={desktopLanguageDropdownRef}>
                <button
                  onClick={() => setIsLanguageDropdownOpen(!isLanguageDropdownOpen)}
                  className="flex items-center space-x-1 px-4 py-2 text-gray-700 rounded-lg hover:bg-gray-100 transition-colors font-medium">
                  <Globe className="w-5 h-5" />
                  <span>{i18n.language.toUpperCase()}</span>
                  <ChevronDown className={`w-4 h-4 transition-transform duration-200 ${isLanguageDropdownOpen ? 'rotate-180' : ''}`} />
                </button>
                {isLanguageDropdownOpen && (
                  <div className="absolute right-0 mt-2 w-32 bg-white rounded-lg shadow-lg border border-gray-200 overflow-hidden z-20">
                    <button onClick={() => changeLanguage('en')} className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">
                      English
                    </button>
                    <button onClick={() => changeLanguage('si')} className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">
                      සිංහල
                    </button>
                  </div>
                )}
              </div>
      
              {/* Profile Dropdown (Desktop) */}
              <div className="relative" ref={desktopProfileDropdownRef}>
                <button onClick={() => setIsProfileDropdownOpen(!isProfileDropdownOpen)}
                  className="flex items-center space-x-2 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors font-medium"><User className="w-5 h-5" />
                  <ChevronDown className={`w-4 h-4 transition-transform duration-200 ${isProfileDropdownOpen ? 'rotate-180' : ''}`} />
                </button>
      
                {/* Dropdown Menu */}
                {isProfileDropdownOpen && (
                  <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 overflow-hidden z-20">
                    <div className="py-1">
                      <div className="px-4 py-3 border-b border-gray-100">
                        <div className="text-sm font-medium text-gray-900">{userInfo.username}</div>
                        <div className="text-sm text-gray-500">{userInfo.email}</div>
                      </div>  
                      <button onClick={handleLogout} className="flex items-center w-full px-4 py-2 text-sm text-red-600 hover:bg-red-50 transition-colors"> <LogOut className="w-4 h-4 mr-3" />
                        {t('nav.logout')}
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>
      
            {/* Mobile Menu Button */}
            <div className="md:hidden flex items-center space-x-4">
              {/* Mobile Language Dropdown (New) */}
              <div className="relative" ref={mobileLanguageDropdownRef}>
                <button
                  onClick={() => setIsLanguageDropdownOpen(!isLanguageDropdownOpen)}
                  className="flex items-center bg-gray-200 text-gray-700 p-2 rounded-lg hover:bg-gray-300 transition-colors">
                  <Globe className="w-5 h-5" />
                </button>
                {isLanguageDropdownOpen && (
                  <div className="absolute right-0 mt-2 w-32 bg-white rounded-lg shadow-lg border border-gray-200 overflow-hidden z-20">
                    <button onClick={() => changeLanguage('en')} className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">
                      English
                    </button>
                    <button onClick={() => changeLanguage('si')} className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">
                      සිංහල
                    </button>
                  </div>
                )}
              </div>
      
              <button onClick={() => setIsMenuOpen(!isMenuOpen)} className="text-gray-700 hover:text-blue-600 transition-colors"> 
                {isMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
              </button>
            </div>
      
            {/* Mobile Menu */}
            {isMenuOpen && (
              <div className="md:hidden absolute top-16 left-0 right-0 bg-white shadow-lg border-t border-gray-200">
                <div className="flex flex-col space-y-0">
                  <Link to="/home" className="px-4 py-3 text-gray-700 hover:bg-gray-50 transition-colors border-b border-gray-100" onClick={() => setIsMenuOpen(false)}>
                    {t('nav.home')}
                  </Link>
                  <Link to="/busroutes" className="px-4 py-3 text-gray-700 hover:bg-gray-50 transition-colors border-b border-gray-100" onClick={() => setIsMenuOpen(false)}>
                    {t('nav.routes')}
                  </Link>
                  <Link to="/buytickets" className="px-4 py-3 text-gray-700 hover:bg-gray-50 transition-colors border-b border-gray-100" onClick={() => setIsMenuOpen(false)}>
                    {t('nav.season')}
                  </Link>
                  <Link to="/help" className="px-4 py-3 text-gray-700 hover:bg-gray-50 transition-colors" onClick={() => setIsMenuOpen(false)}>
                    {t('nav.help')}
                  </Link>
                </div>
              </div>
            )}
          </div>
        </div>
      </nav>
      {/* Hero Section */}
      <div className="relative w-full h-[400px] bg-cover bg-center" 
           style={{ backgroundImage: `url('/images/hero-image.jpg')` }}>
        <div className="absolute inset-0 bg-black/50 flex items-center justify-center">
          <h1 className="text-4xl md:text-5xl font-bold text-white text-center">
            {t('hero.title')}
          </h1>
        </div>
      </div>
    </div>
  );
}
