import React, { useState, useEffect } from 'react';
import { 
  Users, 
  Settings, 
  BarChart3, 
  LogOut, 
  Search,
  Menu,
  UserCheck,
  ShoppingCart,
  TrendingUp,
  Activity,
  RefreshCw
} from 'lucide-react';

export default function Admin() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [searchQuery, setSearchQuery] = useState('');
  const [stats, setStats] = useState({
    totalUsers: 1250,
    activeUsers: 892,
    totalOrders: 3456,
    revenue: 125000
  });

  useEffect(() => {
    // Simulated API call for stats remains unchanged
  }, []);

  const handleLogout = async () => {
    try {
      const confirmed = window.confirm('Are you sure you want to logout?');
      if (!confirmed) return;

      const response = await fetch('/api/auth/logout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include'
      });

      if (response.ok) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        sessionStorage.clear();
        window.location.href = '/login';
      } else {
        console.error('Logout failed, but redirecting anyway');
        window.location.href = '/login';
      }
    } catch (error) {
      console.error('Logout error:', error);
      window.location.href = '/login';
    }
  };

  const StatCard = ({ title, value, icon: Icon, bgColor, iconColor }) => (
    <div className={`${bgColor} rounded-xl shadow-lg p-6 transform hover:scale-105 transition-all duration-300`}>
      <div className="flex items-center justify-between">
        <div>
          <p className="text-sm font-semibold text-gray-700 uppercase tracking-wide">{title}</p>
          <p className="text-3xl font-bold text-gray-900 mt-2">{value.toLocaleString()}</p>
        </div>
        <div className={`p-4 rounded-2xl ${iconColor} shadow-md`}>
          <Icon className="h-8 w-8 text-white" />
        </div>
      </div>
    </div>
  );

  const DashboardContent = () => (
    <div className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Users"
          value={stats.totalUsers}
          icon={Users}
          bgColor="bg-gradient-to-br from-blue-50 to-blue-100"
          iconColor="bg-gradient-to-br from-blue-500 to-blue-600"
        />
        <StatCard
          title="Active Users"
          value={stats.activeUsers}
          icon={UserCheck}
          bgColor="bg-gradient-to-br from-green-50 to-green-100"
          iconColor="bg-gradient-to-br from-green-500 to-green-600"
        />
        <StatCard
          title="Total Orders"
          value={stats.totalOrders}
          icon={ShoppingCart}
          bgColor="bg-gradient-to-br from-purple-50 to-purple-100"
          iconColor="bg-gradient-to-br from-purple-500 to-purple-600"
        />
        <StatCard
          title="Revenue (LKR)"
          value={stats.revenue}
          icon={TrendingUp}
          bgColor="bg-gradient-to-br from-orange-50 to-orange-100"
          iconColor="bg-gradient-to-br from-orange-500 to-orange-600"
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="bg-white rounded-xl shadow-lg p-6 border border-gray-100">
          <h3 className="text-lg font-bold text-gray-900 mb-4 flex items-center">
            <Activity className="h-5 w-5 mr-2 text-blue-600" />
            Recent Activities
          </h3>
          <div className="space-y-3">
            <div className="flex items-center space-x-3 p-4 bg-gradient-to-r from-blue-50 to-blue-100 rounded-xl border border-blue-200 hover:shadow-md transition-all duration-200">
              <div className="p-2 bg-blue-500 rounded-full">
                <Activity className="h-4 w-4 text-white" />
              </div>
              <div className="flex-1">
                <p className="text-sm font-semibold text-gray-900">New user registered</p>
                <p className="text-xs text-gray-500">2 minutes ago</p>
              </div>
              <div className="w-2 h-2 bg-blue-500 rounded-full animate-pulse"></div>
            </div>
            <div className="flex items-center space-x-3 p-4 bg-gradient-to-r from-green-50 to-green-100 rounded-xl border border-green-200 hover:shadow-md transition-all duration-200">
              <div className="p-2 bg-green-500 rounded-full">
                <ShoppingCart className="h-4 w-4 text-white" />
              </div>
              <div className="flex-1">
                <p className="text-sm font-semibold text-gray-900">Order #1234 completed</p>
                <p className="text-xs text-gray-500">15 minutes ago</p>
              </div>
              <div className="w-2 h-2 bg-green-500 rounded-full"></div>
            </div>
            <div className="flex items-center space-x-3 p-4 bg-gradient-to-r from-purple-50 to-purple-100 rounded-xl border border-purple-200 hover:shadow-md transition-all duration-200">
              <div className="p-2 bg-purple-500 rounded-full">
                <Users className="h-4 w-4 text-white" />
              </div>
              <div className="flex-1">
                <p className="text-sm font-semibold text-gray-900">User profile updated</p>
                <p className="text-xs text-gray-500">1 hour ago</p>
              </div>
              <div className="w-2 h-2 bg-purple-500 rounded-full"></div>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-lg p-6 border border-gray-100">
          <h3 className="text-lg font-bold text-gray-900 mb-4 flex items-center">
            <Settings className="h-5 w-5 mr-2 text-green-600" />
            System Status
          </h3>
          <div className="space-y-3">
            <div className="flex items-center justify-between p-4 bg-gradient-to-r from-green-50 to-green-100 rounded-xl border border-green-200">
              <div className="flex items-center space-x-3">
                <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
                <span className="text-sm font-semibold text-gray-900">Database</span>
              </div>
              <span className="px-3 py-1 text-xs font-bold text-green-800 bg-green-200 rounded-full border border-green-300">Online</span>
            </div>
            <div className="flex items-center justify-between p-4 bg-gradient-to-r from-green-50 to-green-100 rounded-xl border border-green-200">
              <div className="flex items-center space-x-3">
                <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
                <span className="text-sm font-semibold text-gray-900">API Server</span>
              </div>
              <span className="px-3 py-1 text-xs font-bold text-green-800 bg-green-200 rounded-full border border-green-300">Running</span>
            </div>
            <div className="flex items-center justify-between p-4 bg-gradient-to-r from-green-50 to-green-100 rounded-xl border border-green-200">
              <div className="flex items-center space-x-3">
                <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
                <span className="text-sm font-semibold text-gray-900">Ballerina Backend</span>
              </div>
              <span className="px-3 py-1 text-xs font-bold text-green-800 bg-green-200 rounded-full border border-green-300">Healthy</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const UsersContent = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Fallback demo data
    const demoUsers = [
      { username: 'Kasun Perera', email: 'kasun@gmail.com', created_at: '2024-01-15T00:00:00Z' },
      { username: 'Nimal Silva', email: 'nimal@gmail.com', created_at: '2024-02-20T00:00:00Z' },
      { username: 'Sunil Fernando', email: 'sunil@gmail.com', created_at: '2024-03-10T00:00:00Z' },
    ];

    const fetchUsers = async () => {
      try {
        setLoading(true);
        setError(null);
        const response = await fetch('/api/users', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            // Uncomment if authentication is required
            // 'Authorization': `Bearer ${localStorage.getItem('token')}`
          },
          credentials: 'include'
        });

        // Log response details for debugging
        console.log('API Response Status:', response.status);
        console.log('API Response Headers:', Object.fromEntries(response.headers.entries()));

        if (!response.ok) {
          const text = await response.text();
          console.error('API Response Text:', text);
          throw new Error(`Failed to fetch users: ${response.status} ${response.statusText}`);
        }

        const contentType = response.headers.get('content-type');
        if (!contentType || !contentType.includes('application/json')) {
          const text = await response.text();
          console.error('Non-JSON Response:', text);
          throw new Error(`Response is not JSON (Status: ${response.status})`);
        }

        const data = await response.json();
        // Validate data structure
        if (!Array.isArray(data) || !data.every(user => user.username && user.email && user.created_at)) {
          throw new Error('Invalid user data structure');
        }
        setUsers(data);
        setLoading(false);
      } catch (err) {
        console.error('Fetch Error:', err);
        setError(`Failed to load users: ${err.message}. Using demo data.`);
        setUsers(demoUsers); // Fallback to demo data
        setLoading(false);
      }
    };

    useEffect(() => {
      fetchUsers();
    }, []);

    const filteredUsers = users.filter(user =>
      user.username.toLowerCase().includes(searchQuery.toLowerCase()) ||
      user.email.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
      <div className="bg-white rounded-lg shadow-md">
        <div className="p-6 border-b border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900">User Management</h3>
        </div>
        <div className="p-6">
          {error && (
            <div className="mb-4 flex items-center justify-between bg-red-100 p-3 rounded-lg">
              <p className="text-red-700 text-sm">{error}</p>
              <button
                onClick={fetchUsers}
                className="flex items-center space-x-2 px-3 py-1 text-blue-600 hover:text-blue-800 bg-blue-50 hover:bg-blue-100 rounded-lg transition-all duration-200"
              >
                <RefreshCw className="h-4 w-4" />
                <span>Retry</span>
              </button>
            </div>
          )}
          <div className="overflow-x-auto">
            {loading ? (
              <p className="text-center text-gray-500">Loading users...</p>
            ) : filteredUsers.length > 0 ? (
              <table className="w-full table-auto">
                <thead>
                  <tr className="bg-gray-50">
                    <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Username</th>
                    <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Email</th>
                    <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Created At</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200">
                  {filteredUsers.map((user, index) => (
                    <tr key={index}>
                      <td className="px-4 py-3 text-sm font-medium text-gray-900">{user.username}</td>
                      <td className="px-4 py-3 text-sm text-gray-500">{user.email}</td>
                      <td className="px-4 py-3 text-sm text-gray-500">{new Date(user.created_at).toLocaleDateString()}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p className="text-center text-gray-500">No users found</p>
            )}
          </div>
        </div>
      </div>
    );
  };

  const OrdersContent = () => (
    <div className="bg-white rounded-lg shadow-md">
      <div className="p-6 border-b border-gray-200">
        <h3 className="text-lg font-semibold text-gray-900">Recent Orders</h3>
      </div>
      <div className="p-6">
        <div className="space-y-4">
          <div className="border rounded-lg p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="font-medium text-gray-900">Order #001</p>
                <p className="text-sm text-gray-500">Kasun Perera • kasun@gmail.com</p>
              </div>
              <div className="text-right">
                <p className="font-medium text-gray-900">LKR 15,000</p>
                <span className="px-2 py-1 text-xs font-medium text-green-800 bg-green-200 rounded-full">Completed</span>
              </div>
            </div>
          </div>
          <div className="border rounded-lg p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="font-medium text-gray-900">Order #002</p>
                <p className="text-sm text-gray-500">Nimal Silva • nimal@gmail.com</p>
              </div>
              <div className="text-right">
                <p className="font-medium text-gray-900">LKR 8,500</p>
                <span className="px-2 py-1 text-xs font-medium text-yellow-800 bg-yellow-200 rounded-full">Processing</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const SettingsContent = () => (
    <div className="bg-white rounded-lg shadow-md">
      <div className="p-6 border-b border-gray-200">
        <h3 className="text-lg font-semibold text-gray-900">System Settings</h3>
      </div>
      <div className="p-6 space-y-4">
        <div className="flex items-center justify-between py-3">
          <div>
            <p className="font-medium text-gray-900">Email Notifications</p>
            <p className="text-sm text-gray-500">Send notifications to admin email</p>
          </div>
          <label className="relative inline-flex items-center cursor-pointer">
            <input type="checkbox" defaultChecked className="sr-only peer" />
            <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-blue-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-blue-600"></div>
          </label>
        </div>
        <div className="flex items-center justify-between py-3">
          <div>
            <p className="font-medium text-gray-900">Auto Backup</p>
            <p className="text-sm text-gray-500">Automatically backup data daily</p>
          </div>
          <label className="relative inline-flex items-center cursor-pointer">
            <input type="checkbox" defaultChecked className="sr-only peer" />
            <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-blue-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-blue-600"></div>
          </label>
        </div>
        <div className="border-t pt-4">
          <button className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors">
            Save Settings
          </button>
        </div>
      </div>
    </div>
  );

  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return <DashboardContent />;
      case 'users':
        return <UsersContent />;
      case 'orders':
        return <OrdersContent />;
      case 'settings':
        return <SettingsContent />;
      default:
        return <DashboardContent />;
    }
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <div className={`w-64 bg-gradient-to-b from-blue-600 via-blue-700 to-blue-800 transform ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out md:translate-x-0 md:static shadow-xl`}>
        <div className="flex items-center justify-center h-16 bg-blue-800 border-b border-blue-600">
          <h1 className="text-white text-xl font-bold tracking-wide">Admin Panel</h1>
        </div>
        <nav className="mt-8 px-4">
          <div className="space-y-2">
            <button
              onClick={() => setActiveTab('dashboard')}
              className={`w-full flex items-center px-4 py-3 text-left rounded-lg transition-all duration-200 ${
                activeTab === 'dashboard' 
                  ? 'bg-blue-500 text-white shadow-lg' 
                  : 'text-blue-100 hover:bg-blue-600 hover:text-white'
              }`}
            >
              <BarChart3 className="mr-3 h-5 w-5" />
              <span className="font-medium">Dashboard</span>
            </button>
            <button
              onClick={() => setActiveTab('users')}
              className={`w-full flex items-center px-4 py-3 text-left rounded-lg transition-all duration-200 ${
                activeTab === 'users' 
                  ? 'bg-blue-500 text-white shadow-lg' 
                  : 'text-blue-100 hover:bg-blue-600 hover:text-white'
              }`}
            >
              <Users className="mr-3 h-5 w-5" />
              <span className="font-medium">Users</span>
            </button>
            <button
              onClick={() => setActiveTab('orders')}
              className={`w-full flex items-center px-4 py-3 text-left rounded-lg transition-all duration-200 ${
                activeTab === 'orders' 
                  ? 'bg-blue-500 text-white shadow-lg' 
                  : 'text-blue-100 hover:bg-blue-600 hover:text-white'
              }`}
            >
              <ShoppingCart className="mr-3 h-5 w-5" />
              <span className="font-medium">Orders</span>
            </button>
            <button
              onClick={() => setActiveTab('settings')}
              className={`w-full flex items-center px-4 py-3 text-left rounded-lg transition-all duration-200 ${
                activeTab === 'settings' 
                  ? 'bg-blue-500 text-white shadow-lg' 
                  : 'text-blue-100 hover:bg-blue-600 hover:text-white'
              }`}
            >
              <Settings className="mr-3 h-5 w-5" />
              <span className="font-medium">Settings</span>
            </button>
          </div>
        </nav>
      </div>
      
      {/* Mobile sidebar overlay */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 bg-gray-600 bg-opacity-50 z-40 md:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Main content */}
      <div className="flex-1 flex flex-col">
        {/* Header */}
        <header className="bg-white shadow-sm border-b border-gray-200 h-16">
          <div className="flex items-center justify-between px-6 h-full">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => setSidebarOpen(!sidebarOpen)}
                className="text-gray-600 hover:text-gray-900 md:hidden p-2 rounded-lg hover:bg-gray-100 transition-all duration-200"
              >
                <Menu className="h-6 w-6" />
              </button>
              <div className="flex items-center bg-gray-50 rounded-lg px-4 py-2 w-full sm:w-96">
                <Search className="h-5 w-5 text-gray-400 mr-3" />
                <input
                  type="text"
                  placeholder="Search users by username or email..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="bg-transparent outline-none flex-1 text-sm font-medium placeholder-gray-500"
                />
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <button
                onClick={handleLogout}
                className="flex items-center space-x-2 px-4 py-2 text-red-600 hover:text-white bg-red-50 hover:bg-red-600 rounded-lg transition-all duration-200 transform hover:scale-105 active:scale-95"
                type="button"
              >
                <LogOut className="h-5 w-5" />
                <span className="hidden sm:inline font-semibold">Logout</span>
              </button>
            </div>
          </div>
        </header>

        {/* Main content area */}
        <main className="flex-1 p-6 overflow-y-auto">
          <div className="mb-6">
            <h1 className="text-2xl font-bold text-gray-900">
              {activeTab.charAt(0).toUpperCase() + activeTab.slice(1)}
            </h1>
            <p className="text-gray-600 mt-1">
              Welcome back! Here's what's happening with your system.
            </p>
          </div>
          
          {renderContent()}
        </main>
      </div>
    </div>
  );
}
