import React, { useState, useEffect } from "react";

export default function Home() {
  const [profile, setProfile] = useState(null);
  const [open, setOpen] = useState(false);

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (userId) {
      fetch(`http://localhost:8080/user/profile?id=${userId}`)
        .then((res) => res.json())
        .then((data) => setProfile(data)) // { username, email }
        .catch((err) => console.error("Profile fetch error:", err));
    }
  }, []);

  const logout = () => {
    localStorage.removeItem("userId");
    window.location.href = "/login";
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Navbar */}
      <nav className="bg-gray-800 text-white px-6 py-3 flex justify-between items-center">
        {/* Left side (logo + nav links) */}
        <div className="flex items-center space-x-6">
          <a href="/" className="text-xl font-bold">
            E-Pass System
          </a>
          <a href="/" className="hover:text-gray-300">
            Home
          </a>
          <a href="/about" className="hover:text-gray-300">
            About
          </a>
        </div>

        {/* Right side (profile dropdown) */}
        <div className="relative">
          <button
            onClick={() => setOpen(!open)}
            className="flex items-center space-x-2 focus:outline-none"
          >
            <img
              src="/profile-icon.png"
              alt="Profile"
              className="w-8 h-8 rounded-full"
            />
            <span className="hidden sm:inline text-sm font-medium">
              {profile ? profile.username : "Loading..."}
            </span>
          </button>

          {open && (
            <div className="absolute right-0 mt-2 w-56 bg-white text-gray-800 rounded-md shadow-lg z-10">
              <div className="px-4 py-3 border-b">
                {profile ? (
                  <>
                    <p className="text-sm font-semibold">{profile.username}</p>
                    <p className="text-xs text-gray-500">{profile.email}</p>
                  </>
                ) : (
                  <p className="text-sm text-gray-500">No profile data</p>
                )}
              </div>
              <button
                onClick={logout}
                className="block w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-gray-100"
              >
                Logout
              </button>
            </div>
          )}
        </div>
      </nav>

      {/* Page content */}
      <main className="p-6">
        <h1 className="text-2xl font-bold text-gray-800">
          Welcome to E-Pass System
        </h1>
        <p className="mt-2 text-gray-600">
          This is your home dashboard. Use the navigation above.
        </p>
      </main>
    </div>
  );
}
