        <div>
          <button className="w-full bg-blue-600 text-white font-semibold py-3 rounded-lg hover:bg-blue-700 transition-colors shadow-lg">
            Search Routes
          </button>
        </div>
      </div>
    </div>

    {/* Featured Routes (Static for now, can be fetched from API later) */}
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
      {/* Route Card 1 */}
      <div className="bg-gray-50 rounded-lg p-6 shadow-md hover:shadow-xl transition-shadow duration-300">
        <h3 className="text-2xl font-bold text-gray-900 mb-2">Colombo - Galle</h3>
        <p className="text-gray-600 mb-4">Southern Expressway (E01)</p>
        <div className="flex justify-between items-center text-sm text-gray-500 mb-4">
          <span><Clock className="inline-block w-4 h-4 mr-1 text-blue-500" /> ~2 hours</span>
          <span><Bus className="inline-block w-4 h-4 mr-1 text-blue-500" /> Route 100</span>
        </div>
        <Link to="/buytickets" className="block w-full text-center bg-blue-600 text-white font-medium py-3 rounded-lg hover:bg-blue-700 transition-colors">
          View & Buy Passes
        </Link>
      </div>

      {/* Route Card 2 */}
      <div className="bg-gray-50 rounded-lg p-6 shadow-md hover:shadow-xl transition-shadow duration-300">
        <h3 className="text-2xl font-bold text-gray-900 mb-2">Colombo - Matara</h3>
        <p className="text-gray-600 mb-4">Southern Expressway (E01)</p>
        <div className="flex justify-between items-center text-sm text-gray-500 mb-4">
          <span><Clock className="inline-block w-4 h-4 mr-1 text-blue-500" /> ~2.5 hours</span>
          <span><Bus className="inline-block w-4 h-4 mr-1 text-blue-500" /> Route 101</span>
        </div>
        <Link to="/buytickets" className="block w-full text-center bg-blue-600 text-white font-medium py-3 rounded-lg hover:bg-blue-700 transition-colors">
          View & Buy Passes
        </Link>
      </div>

      {/* Route Card 3 */}
      <div className="bg-gray-50 rounded-lg p-6 shadow-md hover:shadow-xl transition-shadow duration-300">
        <h3 className="text-2xl font-bold text-gray-900 mb-2">Colombo - Katunayake</h3>
        <p className="text-gray-600 mb-4">Colombo-Katunayake Expressway (E03)</p>
        <div className="flex justify-between items-center text-sm text-gray-500 mb-4">
          <span><Clock className="inline-block w-4 h-4 mr-1 text-blue-500" /> ~45 minutes</span>
          <span><Bus className="inline-block w-4 h-4 mr-1 text-blue-500" /> Route 102</span>
        </div>
        <Link to="/buytickets" className="block w-full text-center bg-blue-600 text-white font-medium py-3 rounded-lg hover:bg-blue-700 transition-colors">
          View & Buy Passes
        </Link>
      </div>

    </div>
  </div>
</section>

{/* How it Works Section */}
<section className="py-16 bg-gray-50">
  <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
    <div className="text-center mb-12">
      <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
        Seamless Travel, QR-Enabled
      </h2>
      <p className="text-lg text-gray-600">
        Our system allows you to pay once and travel multiple times by simply scanning a QR code.
      </p>
    </div>
    <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
      <div className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center text-center">
        <div className="w-16 h-16 rounded-full bg-blue-100 flex items-center justify-center mb-4">
          <Smartphone className="w-8 h-8 text-blue-600" />
        </div>
        <h3 className="text-xl font-semibold text-gray-900 mb-2">Buy Your Pass</h3>
        <p className="text-gray-600">Select a weekly, monthly, or a single-trip pass for your route.</p>
      </div>
      <div className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center text-center">
        <div className="w-16 h-16 rounded-full bg-blue-100 flex items-center justify-center mb-4">
          <CheckCircle className="w-8 h-8 text-blue-600" />
        </div>
        <h3 className="text-xl font-semibold text-gray-900 mb-2">Receive QR Code</h3>
        <p className="text-gray-600">Your digital pass with a unique QR code is instantly sent to your app.</p>
      </div>
      <div className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center text-center">
        <div className="w-16 h-16 rounded-full bg-blue-100 flex items-center justify-center mb-4">
          <Bus className="w-8 h-8 text-blue-600" />
        </div>
        <h3 className="text-xl font-semibold text-gray-900 mb-2">Scan & Go</h3>
        <p className="text-gray-600">Simply scan your QR code at the bus entry point and enjoy your ride.</p>
      </div>
    </div>
  </div>
</section>

{/* Footer */}
<footer className="bg-gray-900 text-white py-12 px-4 sm:px-6 lg:px-8">
  <div className="max-w-7xl mx-auto">
    <div className="grid grid-cols-1 md:grid-cols-4 gap-8 mb-8">
      <div className="col-span-1 md:col-span-2">
        <div className="flex items-center space-x-3 mb-4">
          <div className="w-10 h-10 bg-gradient-to-r from-blue-600 to-purple-600 rounded-lg flex items-center justify-center">
            <Bus className="w-6 h-6 text-white" />
          </div>
          <span className="text-xl font-bold">eWay Bus</span>
        </div>
        <p className="text-gray-400 mb-6 max-w-md">
          Sri Lanka's premier digital pre-purchased bus ticket system. Pay once, travel effortlessly with eWay Bus.
        </p>
        <div className="flex space-x-4">
          <div className="w-10 h-10 bg-gray-800 rounded-full flex items-center justify-center hover:bg-blue-600 transition-colors cursor-pointer">
