import React, { useState, useEffect } from 'react';
import { 
  Users, 
  Settings, 
  BarChart3, 
  LogOut, 
  Search,
  Menu,
  UserCheck,
  ShoppingCart,
  TrendingUp,
  Activity,
  RefreshCw,
  AlertCircle,
  CheckCircle
} from 'lucide-react';

export default function Admin() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [searchQuery, setSearchQuery] = useState('');
  const [stats, setStats] = useState({
    totalUsers: 0,
    activeUsers: 0,
    totalOrders: 0,
    revenue: 0
  });

  useEffect(() => {
    // Fetch dashboard stats when component loads
    fetchDashboardStats();
  }, []);

  const fetchDashboardStats = async () => {
    try {
      // Try Ballerina stats endpoint
      const response = await fetch('http://localhost:8081/stats', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setStats({
          totalUsers: data.totalUsers || data.total_users || 0,
          activeUsers: data.activeUsers || data.active_users || 0,
          totalOrders: data.totalOrders || data.total_orders || 0,
          revenue: data.revenue || 0
        });
      }
    } catch (error) {
      console.error('Failed to fetch dashboard stats from Ballerina service:', error);
    }
  };

  const handleLogout = async () => {
    try {
      const confirmed = window.confirm('Are you sure you want to logout?');
      if (!confirmed) return;

      const response = await fetch('http://localhost:8081/api/auth/logout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include'
      });

      if (response.ok) {
        window.location.href = '/login';
      } else {
        console.error('Logout failed, but redirecting anyway');
        window.location.href = '/login';
      }
    } catch (error) {
      console.error('Logout error:', error);
      window.location.href = '/login';
    }
  };

  const StatCard = ({ title, value, icon: Icon, bgColor, iconColor }) => (
    <div className={`${bgColor} rounded-xl shadow-lg p-6 transform hover:scale-105 transition-all duration-300`}>
      <div className="flex items-center justify-between">
        <div>
          <p className="text-sm font-semibold text-gray-700 uppercase tracking-wide">{title}</p>
          <p className="text-3xl font-bold text-gray-900 mt-2">{value.toLocaleString()}</p>
        </div>
        <div className={`p-4 rounded-2xl ${iconColor} shadow-md`}>
          <Icon className="h-8 w-8 text-white" />
        </div>
      </div>
    </div>
  );

  const DashboardContent = () => (
    <div className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Users"
          value={stats.totalUsers}
          icon={Users}
          bgColor="bg-gradient-to-br from-blue-50 to-blue-100"
          iconColor="bg-gradient-to-br from-blue-500 to-blue-600"
        />
        <StatCard
          title="Active Users"
          value={stats.activeUsers}
          icon={UserCheck}
          bgColor="bg-gradient-to-br from-green-50 to-green-100"
          iconColor="bg-gradient-to-br from-green-500 to-green-600"
        />
        <StatCard
          title="Total Orders"
          value={stats.totalOrders}
          icon={ShoppingCart}
          bgColor="bg-gradient-to-br from-purple-50 to-purple-100"
          iconColor="bg-gradient-to-br from-purple-500 to-purple-600"
        />
        <StatCard
          title="Revenue (LKR)"
          value={stats.revenue}
          icon={TrendingUp}
          bgColor="bg-gradient-to-br from-orange-50 to-orange-100"
          iconColor="bg-gradient-to-br from-orange-500 to-orange-600"
        />
      </div>
    </div>
  );

  const UsersContent = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [connectionStatus, setConnectionStatus] = useState('checking');

    const checkBackendConnection = async () => {
      try {
        // Try common Ballerina health check endpoints
        const healthEndpoints = [
          'http://localhost:8081/health',
          'http://localhost:8081/api/health',
          'http://localhost:8081/'
        ];

        for (const endpoint of healthEndpoints) {
          try {
            const response = await fetch(endpoint, {
              method: 'GET',
              headers: {
                'Content-Type': 'application/json',
              }
            });
            
            if (response.ok) {
              setConnectionStatus('connected');
              return true;
            }
          } catch (err) {
            // Continue to next endpoint
          }
        }
        
        setConnectionStatus('disconnected');
        return false;
      } catch (error) {
        setConnectionStatus('disconnected');
        return false;
      }
    };

    const fetchUsers = async () => {
      try {
        setLoading(true);
        setError(null);

        // First check if backend is running
        const isConnected = await checkBackendConnection();
        if (!isConnected) {
          throw new Error('Ballerina backend server is not running on port 8081');
        }

        // Try Ballerina-specific endpoints
        const endpoints = [
          'http://localhost:8081/users',           // Most common Ballerina pattern
          'http://localhost:8081/api/users',       // API prefix
          'http://localhost:8081/user/users',      // Service/resource pattern
          'http://localhost:8081/admin/users'      // Admin-specific endpoint
        ];

        let response;
        let lastError;

        for (const endpoint of endpoints) {
          try {
            console.log(`Trying Ballerina endpoint: ${endpoint}`);
            response = await fetch(endpoint, {
              method: 'GET',
              headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
                'Access-Control-Allow-Origin': '*'
              }
            });

            if (response.ok) {
              console.log(`Successfully connected to Ballerina service: ${endpoint}`);
              break;
            } else {
              console.log(`Ballerina endpoint ${endpoint} returned ${response.status}: ${response.statusText}`);
            }
          } catch (err) {
            console.log(`Ballerina endpoint ${endpoint} failed:`, err.message);
            lastError = err;
          }
        }

        if (!response || !response.ok) {
          throw new Error(`All Ballerina endpoints failed. Make sure your Ballerina service is running with: bal run main.bal`);
        }

        const contentType = response.headers.get('content-type');
        if (!contentType || !contentType.includes('application/json')) {
          const text = await response.text();
          console.log('Non-JSON response:', text.substring(0, 200));
          throw new Error('Server returned non-JSON response. Check your backend API.');
        }

        const data = await response.json();
        console.log('Received users data:', data);

        // Handle different response formats
        let usersArray = [];
        if (Array.isArray(data)) {
          usersArray = data;
        } else if (data.users && Array.isArray(data.users)) {
          usersArray = data.users;
        } else if (data.data && Array.isArray(data.data)) {
          usersArray = data.data;
        } else {
          throw new Error('Invalid response format: expected array of users');
        }

        // Validate and clean user data
        const validUsers = usersArray.filter(u => {
          return u && typeof u === 'object' && (u.username || u.name) && u.email;
        }).map(user => ({
          id: user.id || user._id || Math.random(),
          username: user.username || user.name || 'Unknown',
          email: user.email,
          created_at: user.created_at || user.createdAt || user.date_created,
          status: user.status || 'active',
          role: user.role || 'user'
        }));

        if (validUsers.length === 0) {
          throw new Error('No valid user records found in database');
        }

        setUsers(validUsers);
        setConnectionStatus('connected');
        
        // Update dashboard stats
        setStats(prev => ({
          ...prev,
          totalUsers: validUsers.length,
          activeUsers: validUsers.filter(u => u.status === 'active').length
        }));

      } catch (err) {
        console.error('Fetch Error:', err);
        setError(err.message);
        setUsers([]);
        setConnectionStatus('error');
      } finally {
        setLoading(false);
      }
    };

    useEffect(() => {
      fetchUsers();
    }, []);

    const filteredUsers = users.filter(user =>
      user.username.toLowerCase().includes(searchQuery.toLowerCase()) ||
      user.email.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
      <div className="space-y-4">
        {/* Connection Status */}
        <div className="flex items-center space-x-2 mb-4">
          {connectionStatus === 'connected' && (
            <div className="flex items-center space-x-2 text-green-600">
              <CheckCircle className="h-4 w-4" />
              <span className="text-sm font-medium">Connected to database</span>
            </div>
          )}
          {connectionStatus === 'disconnected' && (
            <div className="flex items-center space-x-2 text-red-600">
              <AlertCircle className="h-4 w-4" />
              <span className="text-sm font-medium">Backend server not running</span>
            </div>
          )}
          {connectionStatus === 'checking' && (
            <div className="flex items-center space-x-2 text-yellow-600">
              <Activity className="h-4 w-4 animate-spin" />
              <span className="text-sm font-medium">Checking connection...</span>
            </div>
          )}
        </div>

        <div className="bg-white rounded-lg shadow-md">
          <div className="p-6 border-b border-gray-200">
            <div className="flex justify-between items-center">
              <h3 className="text-lg font-semibold text-gray-900">User Management</h3>
              <div className="text-sm text-gray-500">
                {loading ? 'Loading...' : `${filteredUsers.length} users found`}
              </div>
            </div>
          </div>
          
          <div className="p-6">
            {error && (
              <div className="mb-4 space-y-3">
                <div className="flex items-start justify-between bg-red-50 border border-red-200 p-4 rounded-lg">
                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-2">
                      <AlertCircle className="h-5 w-5 text-red-600" />
                      <p className="text-red-800 font-semibold">Connection Error</p>
                    </div>
                    <p className="text-red-700 text-sm mb-3">{error}</p>
                    
                    <div className="text-red-700 text-sm space-y-1">
                      <p className="font-medium">Ballerina Backend Solutions:</p>
                      <ul className="list-disc list-inside space-y-1 text-xs">
                        <li>Start your Ballerina service: <code className="bg-red-100 px-1 rounded">bal run main.bal</code></li>
                        <li>Make sure service is running on port 8081</li>
                        <li>Check if your service has a <code>/users</code> resource</li>
                        <li>Verify CORS is configured in your Ballerina service</li>
                        <li>Ensure database connection is working in Ballerina</li>
                        <li>Check Ballerina service logs for errors</li>
                      </ul>
                    </div>
                  </div>
                  
                  <button
                    onClick={fetchUsers}
                    className="flex items-center space-x-2 px-3 py-2 text-blue-600 hover:text-blue-800 bg-blue-50 hover:bg-blue-100 rounded-lg transition-all duration-200 ml-4"
                  >
                    <RefreshCw className="h-4 w-4" />
                    <span>Retry</span>
                  </button>
                </div>
              </div>
            )}

            <div className="overflow-x-auto">
              {loading ? (
                <div className="text-center py-8">
                  <div className="inline-flex items-center space-x-2 text-gray-600">
                    <Activity className="h-5 w-5 animate-spin" />
                    <span>Loading users from database...</span>
                  </div>
                </div>
              ) : filteredUsers.length > 0 ? (
                <table className="w-full table-auto">
                  <thead>
                    <tr className="bg-gray-50">
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ID</th>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Username</th>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Email</th>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Role</th>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Created</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {filteredUsers.map((user) => (
                      <tr key={user.id} className="hover:bg-gray-50 transition-colors duration-150">
                        <td className="px-4 py-3 text-sm text-gray-900 font-mono">
                          {String(user.id).length > 10 ? `${String(user.id).substring(0, 10)}...` : user.id}
                        </td>
                        <td className="px-4 py-3 text-sm font-medium text-gray-900">{user.username}</td>
                        <td className="px-4 py-3 text-sm text-gray-600">{user.email}</td>
                        <td className="px-4 py-3 text-sm">
                          <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                            user.role === 'admin' ? 'bg-purple-100 text-purple-800' : 'bg-gray-100 text-gray-800'
                          }`}>
                            {user.role}
                          </span>
                        </td>
                        <td className="px-4 py-3 text-sm">
                          <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                            user.status === 'active' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                          }`}>
                            {user.status}
                          </span>
                        </td>
                        <td className="px-4 py-3 text-sm text-gray-500">
                          {user.created_at ? new Date(user.created_at).toLocaleDateString('en-GB') : 'N/A'}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <div className="text-center py-8">
                  <Users className="mx-auto h-12 w-12 text-gray-400" />
                  <h3 className="mt-2 text-sm font-medium text-gray-900">No users found</h3>
                  <p className="mt-1 text-sm text-gray-500">
                    {searchQuery ? 'Try adjusting your search terms' : 'Your database appears to be empty'}
                  </p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  };

  const OrdersContent = () => (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h3 className="text-lg font-semibold text-gray-900 mb-4">Order Management</h3>
      <div className="text-center py-8">
        <ShoppingCart className="mx-auto h-12 w-12 text-gray-400" />
        <p className="mt-2 text-gray-500">Orders module coming soon...</p>
        <p className="text-sm text-gray-400">Connect your orders API endpoint to display order data</p>
      </div>
    </div>
  );

  const SettingsContent = () => (
    <div className="bg-white rounded-lg shadow-md">
      <div className="p-6 border-b border-gray-200">
        <h3 className="text-lg font-semibold text-gray-900">System Settings</h3>
      </div>
      <div className="p-6 space-y-6">
        <div className="flex items-center justify-between py-3 border-b border-gray-100">
          <div>
            <p className="font-medium text-gray-900">Email Notifications</p>
            <p className="text-sm text-gray-500">Send notifications to admin email</p>
          </div>
          <input type="checkbox" defaultChecked className="rounded" />
        </div>
        <div className="flex items-center justify-between py-3 border-b border-gray-100">
          <div>
            <p className="font-medium text-gray-900">Auto Backup</p>
            <p className="text-sm text-gray-500">Automatically backup database daily</p>
          </div>
          <input type="checkbox" className="rounded" />
        </div>
        <div className="flex items-center justify-between py-3">
          <div>
            <p className="font-medium text-gray-900">Maintenance Mode</p>
            <p className="text-sm text-gray-500">Put the system in maintenance mode</p>
          </div>
          <input type="checkbox" className="rounded" />
        </div>
      </div>
    </div>
  );

  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return <DashboardContent />;
      case 'users':
        return <UsersContent />;
      case 'orders':
        return <OrdersContent />;
      case 'settings':
        return <SettingsContent />;
      default:
        return <DashboardContent />;
    }
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <div className={`w-64 bg-gradient-to-b from-blue-600 via-blue-700 to-blue-800 transform ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out md:translate-x-0 md:static shadow-xl`}>
        <div className="flex items-center justify-center h-16 bg-blue-800 border-b border-blue-600">
          <h1 className="text-white text-xl font-bold tracking-wide">Admin Panel</h1>
        </div>
        <nav className="mt-8 px-4">
          <div className="space-y-2">
            <button
              onClick={() => setActiveTab('dashboard')}
              className={`w-full flex items-center px-4 py-3 text-left rounded-lg transition-all duration-200 ${activeTab === 'dashboard' ? 'bg-blue-500 text-white shadow-lg' : 'text-blue-100 hover:bg-blue-600 hover:text-white'}`}
            >
              <BarChart3 className="mr-3 h-5 w-5" />
              <span className="font-medium">Dashboard</span>
            </button>
            <button
              onClick={() => setActiveTab('users')}
              className={`w-full flex items-center px-4 py-3 text-left rounded-lg transition-all duration-200 ${activeTab === 'users' ? 'bg-blue-500 text-white shadow-lg' : 'text-blue-100 hover:bg-blue-600 hover:text-white'}`}
            >
              <Users className="mr-3 h-5 w-5" />
              <span className="font-medium">Users</span>
            </button>
            <button
              onClick={() => setActiveTab('orders')}
              className={`w-full flex items-center px-4 py-3 text-left rounded-lg transition-all duration-200 ${activeTab === 'orders' ? 'bg-blue-500 text-white shadow-lg' : 'text-blue-100 hover:bg-blue-600 hover:text-white'}`}
            >
              <ShoppingCart className="mr-3 h-5 w-5" />
              <span className="font-medium">Orders</span>
            </button>
            <button
              onClick={() => setActiveTab('settings')}
              className={`w-full flex items-center px-4 py-3 text-left rounded-lg transition-all duration-200 ${activeTab === 'settings' ? 'bg-blue-500 text-white shadow-lg' : 'text-blue-100 hover:bg-blue-600 hover:text-white'}`}
            >
              <Settings className="mr-3 h-5 w-5" />
              <span className="font-medium">Settings</span>
            </button>
          </div>
        </nav>
      </div>

      {/* Mobile overlay */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 bg-gray-600 bg-opacity-50 z-40 md:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        <header className="bg-white shadow-sm border-b border-gray-200 h-16">
          <div className="flex items-center justify-between px-6 h-full">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => setSidebarOpen(!sidebarOpen)}
                className="text-gray-600 hover:text-gray-900 md:hidden p-2 rounded-lg hover:bg-gray-100 transition-all duration-200"
              >
                <Menu className="h-6 w-6" />
              </button>
              <div className="flex items-center bg-gray-50 rounded-lg px-4 py-2 w-full sm:w-96">
                <Search className="h-5 w-5 text-gray-400 mr-3" />
                <input
                  type="text"
                  placeholder="Search users by username or email..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="bg-transparent outline-none flex-1 text-sm font-medium placeholder-gray-500"
                />
              </div>
            </div>

            <button
              onClick={handleLogout}
              className="flex items-center space-x-2 px-4 py-2 text-red-600 hover:text-white bg-red-50 hover:bg-red-600 rounded-lg transition-all duration-200"
              type="button"
            >
              <LogOut className="h-5 w-5" />
              <span className="hidden sm:inline font-semibold">Logout</span>
            </button>
          </div>
        </header>

        <main className="flex-1 p-6 overflow-y-auto">
          <div className="mb-6">
            <h1 className="text-2xl font-bold text-gray-900">
              {activeTab.charAt(0).toUpperCase() + activeTab.slice(1)}
            </h1>
            <p className="text-gray-600 mt-1">
              Welcome back! Here's what's happening with your system.
            </p>
          </div>
          {renderContent()}
        </main>
      </div>
    </div>
  );
}
